import React, { useEffect, useState } from 'react';

const LiveArena = () => {
  const [matches, setMatches] = useState([]); // Holds all match data
  const [loading, setLoading] = useState(true); // Track loading state

  useEffect(() => {
    const fetchData = async () => {
      const url = 'https://www.rtl.lu/api/sportstream/livestreams?start_date=2024-09-03&end_date=2024-09-10&types=football,handball&source=live-arena';
      try {
        const response = await fetch(url);
        const data = await response.json();
        console.log('API Response:', data); // Log to inspect structure
        setMatches(data); // Store the entire data object
        setLoading(false); // Stop loading when data is fetched
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false); // Stop loading even if there's an error
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <p>Loading matches...</p>;
  }

  // Helper function to render match details
  const renderMatches = (sportType, competition, entries) => {
    return Object.keys(entries).map((date) => (
      <div key={date}>
        {/* Display the match date */}
        <h3>{new Date(date).toLocaleDateString('en-GB', {
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        })}</h3>

        {/* Loop through all matches for the specific date */}
        {entries[date].map((match, index) => (
          <div key={index} className="match-row" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '10px 0' }}>
            {/* Match Time */}
            <div>{match.video.match.hour}</div>

            {/* Match Date */}
            <div>{new Date(match.video.match.date).toLocaleDateString('en-GB')}</div>

            {/* Home Team Logo (if available) */}
            <div>
              <img src={match.video.match.home_team.logo_url || '/default-logo.png'} alt={match.video.match.home_team.name} width="30" />
            </div>

            {/* Home Team Name */}
            <div>{match.video.match.home_team.name}</div>

            {/* Score */}
            <div>{match.video.match['home_score']} - {match.video.match['visitor_score']}</div>

            {/* Visitor Team Name */}
            <div>{match.video.match.visitor_team.name}</div>

            {/* Visitor Team Logo (if available) */}
            <div>
              <img src={match.video.match.visitor_team.logo || '/default-logo.png'} alt={match.video.match.visitor_team.name} width="30" />
            </div>
          </div>
        ))}
        <hr />
      </div>
    ));
  };

  return (
    <div>
      {/* Iterate through all sports (football, handball, etc.) */}
      {Object.keys(matches).map((sportType) => (
        <div key={sportType} className="sport-block">
          {/* Display the sport type */}
          <h2>Sport Type: {sportType.charAt(0).toUpperCase() + sportType.slice(1)}</h2>

          {/* Loop through competitions within the sport */}
          {Object.keys(matches[sportType]).map((competitionKey) => (
            <div key={competitionKey}>
              {/* Display the competition name */}
              <h3>Competition: {matches[sportType][competitionKey].competition.name}</h3>

              {/* Render matches for this competition */}
              {renderMatches(
                sportType,
                matches[sportType][competitionKey].competition,
                matches[sportType][competitionKey].entries
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default LiveArena;
