  const handleSportChange = (event) => {
    setSelectedSport(event.target.value);
    setSelectedCompetition(''); // Reset competition when sport changes
  };

  const handleCompetitionChange = (event) => {
    setSelectedCompetition(event.target.value);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const filteredByDate = (data) => {
    if (!selectedDate) return data;

    const formattedDate = selectedDate.toISOString().split('T')[0];
    const filtered = {};

    Object.keys(data).forEach(sportType => {
      filtered[sportType] = {};
      Object.keys(data[sportType]).forEach(competitionKey => {
        const entries = data[sportType][competitionKey].entries;
        const filteredEntries = {};

        Object.keys(entries).forEach(date => {
          if (date === formattedDate) {
            filteredEntries[date] = entries[date];
          }
        });

        if (Object.keys(filteredEntries).length) {
          filtered[sportType][competitionKey] = { entries: filteredEntries };
        }
      });

      if (Object.keys(filtered[sportType]).length === 0) {
        delete filtered[sportType];
      }
    });

    return filtered;
  };

  const sortedMatches = (data) => {
    if (!selectedDate) return data;

    const filtered = filteredByDate(data);

    return Object.keys(filtered).reduce((acc, sportType) => {
      acc[sportType] = {};
      Object.keys(filtered[sportType]).forEach(competitionKey => {
        acc[sportType][competitionKey] = { entries: filtered[sportType][competitionKey].entries };
      });
      return acc;
    }, {});
  };

  const matchesToDisplay = selectedDate ? sortedMatches(fetchedData) : fetchedData;

  return (
    <div>
      <div className='header-image'>
        <img src='/images/header-image.png' alt='header-image-rtl'/>
      </div>
      <div className='line-1'></div>
      <div className='content-header'>
        <h3>Replay</h3>
        <div className='inputs-div'>
          <label htmlFor='sport-select'>Select Sport:</label>
          <select id='sport-select' value={selectedSport} onChange={handleSportChange}>
            <option value='all'>All</option>
            <option value='football'>Football</option>
            <option value='handball'>Handball</option>
            <option value='basketball'>Basketball</option>
            <option value='volleyball'>Volleyball</option>
            <option value='table-tennis'>Table Tennis</option>
            <option value='cycling'>Cycling</option>
            {/* Add more sports options as needed */}
          </select>
          <label htmlFor='date-select'>Select Date:</label>
          <DatePicker
            id='date-select'
            selected={selectedDate}
            onChange={handleDateChange}
            minDate={new Date('2024-09-03')}
            maxDate={new Date('2024-09-10')}
            filterDate={date => datesWithMatches.some(d => d.toDateString() === date.toDateString())}
          />
        </div>
      </div>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        Object.keys(matchesToDisplay).map((sportType) => (
          <div key={sportType} className="match-header">
            <div className="sport-type-div">
              <h2 className="sport-type">{sportType.charAt(0).toUpperCase() + sportType.slice(1)}</h2>
            </div>
            {Object.keys(matchesToDisplay[sportType]).map((competitionKey) => (
              <div key={competitionKey} className="competition-section">
                <div className="competition-name">
                  <h3>{matchesToDisplay[sportType][competitionKey].competition.name}</h3>
                </div>
                {renderData(matchesToDisplay[sportType][competitionKey].entries)}
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

