import React, { useEffect, useState } from 'react';

const LiveArena = () => {
  const [matches, setMatches] = useState([]); // Holds all match data
  const [filteredMatches, setFilteredMatches] = useState([]); // Filtered/sorted match data

  useEffect(() => {
    // Fetch API data when component mounts
    const fetchData = async () => {
      const url = `https://www.rtl.lu/api/sportstream/livestreams?start_date=2024-09-03&end_date=2024-09-10&types=football,basketball,handball&source=live-arena`;
      try {
        const response = await fetch(url);
        const data = await response.json();
        setMatches(data);
        setFilteredMatches(data); // Initially, show all matches
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {filteredMatches.length > 0 ? (
        filteredMatches.map((match, index) => (
          <div key={index} className="sport-block">
            {/* Sport Type */}
            {index === 0 || filteredMatches[index - 1].type !== match.type ? (
              <div className="sport-header">
                <h2>Type Sport: {match.type.charAt(0).toUpperCase() + match.type.slice(1)}</h2>
              </div>
            ) : null}

            {/* Competition Name */}
            <h3>Ã‰ierepromotioun</h3>

            {/* Match Date */}
            <p>Date: {new Date(match.start_date).toLocaleDateString('de-DE', { day: 'numeric', month: 'long', year: 'numeric' })}</p>

            {/* Match Details in a Row */}
            <div className="match-row" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '10px 0' }}>
              {/* Time */}
              <div>{new Date(match.start_date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</div>

              {/* Date */}
              <div>{new Date(match.start_date).toLocaleDateString()}</div>

              {/* Team A */}
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={match.teamA_logo} alt={match.teamA} style={{ width: '50px', marginRight: '10px' }} />
                <div>{match.teamA}</div>
              </div>

              {/* Score */}
              <div>{match.score}</div>

              {/* Team B */}
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <div>{match.teamB}</div>
                <img src={match.teamB_logo} alt={match.teamB} style={{ width: '50px', marginLeft: '10px' }} />
              </div>
            </div>

            {/* Divider Line */}
            <hr />
          </div>
        ))
      ) : (
        <p>Loading matches...</p>
      )}
    </div>
  );
};

export default Live;
