import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const LiveArena = () => {
  const [fetchedData, setFetchedData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [filterBy, setFilterBy] = useState('sport'); 
  const [sportType, setSportType] = useState('all');

  // Function to fetch data from the API
  const fetchData = async () => {
    setLoading(true); // Set loading state to true when fetching starts
    try {
      const response = await fetch(
        `https://api.example.com/sports?filter=${filterBy}&date=${selectedDate.toISOString().split('T')[0]}&sport=${sportType}`
      );
      const data = await response.json();
      console.log('API Response:', data);
      setFetchedData(data); // Update state with the fetched data
    } catch (error) {
      console.error('Error fetching data:', error);
      setFetchedData(null); // Clear data on error
    } finally {
      setLoading(false); // Set loading state to false after fetch is complete
    }
  };

  // Use useEffect to fetch data when component mounts and when dependencies change
  useEffect(() => {
    fetchData(); // Fetch data whenever selectedDate, filterBy, or sportType changes
  }, [selectedDate, filterBy, sportType]);

  // Format the date for display
  const formatDate = (date) => {
    const options = { day: '2-digit', month: 'long', year: 'numeric' };
    const formattedDate = new Intl.DateTimeFormat('en-GB', options).format(new Date(date));
    return formattedDate.replace(/^(\d{2})/, '$1.');
  };

  // Render the data fetched from the API
  const renderData = (entries) => {
    return Object.keys(entries).map((date) => (
      <div key={date}>
        <div className="match-date-header">
          {formatDate(date)}
        </div>
        {entries[date].map((match, index) => (
          <div key={index} className="match-row">
            <div className="match-row-left">
              <div className="match-time">
                <div className="match-hour">{match.video.match.hour}</div>
                <div className="match-date">{new Date(match.video.match.date).toLocaleDateString('en-GB')}</div>
              </div>
              <div className='team-image-left'>
                <img src={match.video.match.home_team.logo_url} alt={match.video.match.home_team.name} width="60" />
              </div>
              <div>{match.video.match.home_team.name}</div>
            </div>
            <div className="match-score">
              <div>
                {match.video.match['home_score']} : {match.video.match['visitor_score']}
              </div>
              <div className='play-div'>
