import React, { useEffect, useState } from 'react';

const LiveArena = () => {
  const [fetchedData, setFetchedData] = useState([]); 
  const [loading, setLoading] = useState(true); 

  useEffect(() => {
    const fetchData = async () => {
      const url = 'https://www.rtl.lu/api/sportstream/livestreams?start_date=2024-09-03&end_date=2024-09-10&types=football,handball&source=live-arena';
      try {
        const response = await fetch(url);
        const data = await response.json();
        console.log('API Response:', data); 
        setFetchedData(data); 
        setLoading(false); 
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false); 
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <p>Loading matches...</p>;
  }

  const renderData = (sportType, competition, entries) => {
    return Object.keys(entries).map((date) => (
      <div key={date}>
        <h3>{new Date(date).toLocaleDateString('en-GB', {
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        })}</h3>

        {entries[date].map((match, index) => (
          <div key={index} className="match-row">
            <div className='match-row-left'>
              <div className='match-time'>
                <div className='match-hour'>{match.video.match.hour}</div>
                <div className='match-date'>{new Date(match.video.match.date).toLocaleDateString('en-GB')}</div>
              </div>
              <div>
                <img src={match.video.match.home_team.logo_url} alt={match.video.match.home_team.name} width="30" />
              </div>
              <div>{match.video.match.home_team.name}</div>
            </div>
            <div className='match-score'>{match.video.match['home_score']} - {match.video.match['visitor_score']}</div>
            <div className='match-row-right'>
              <div>{match.video.match.visitor_team.name}</div>
              <div>
                <img src={match.video.match.visitor_team.logo_url} alt={match.video.match.visitor_team.name} width="30" />
              </div>
            </div>
          </div>
        ))}
      </div>
    ));
  };

  return (
    <div>
      {Object.keys(fetchedData).map((sportType) => (
        <div key={sportType} className="match-header">
          <div className='sport-type-div'>
            <h2 className='sport-type'>{sportType.charAt(0).toUpperCase() + sportType.slice(1)}</h2>
          </div>
          {Object.keys(fetchedData[sportType]).map((competitionKey) => (
            <div key={competitionKey}>
              <div className='competition-name'>
              </div>
              {renderData(
                sportType,
                fetchedData[sportType][competitionKey].competition,
                fetchedData[sportType][competitionKey].entries
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default LiveArena;
