import React, { useEffect, useState } from 'react';

const LiveArena = () => {
  const [matches, setMatches] = useState([]); // Holds all match data
  const [filteredMatches, setFilteredMatches] = useState([]); // Filtered/sorted match data
  const [loading, setLoading] = useState(true); // Track loading state

  useEffect(() => {
    // Fetch API data when component mounts
    const fetchData = async () => {
      const url = `https://www.rtl.lu/api/sportstream/livestreams?start_date=2024-09-03&end_date=2024-09-10&types=football,basketball,handball&source=live-arena`;
      try {
        const response = await fetch(url);
        const data = await response.json();

        console.log('API Response:', data); // Check if data is being received

        setMatches(data);
        setFilteredMatches(data); // Initially, show all matches
        setLoading(false); // Stop loading when data is fetched
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false); 
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <p>Loading matches...</p>;
  }

  return (
    <div>

    </div>
  );
};

export default LiveArena;
