  const [filteredMatches, setFilteredMatches] = useState([]); // Filtered/sorted match data
  const [loading, setLoading] = useState(true); // Track loading state

  useEffect(() => {
    // Fetch API data when component mounts
    const fetchData = async () => {
      const url = 'https://www.rtl.lu/api/sportstream/livestreams?start_date=2024-09-03&end_date=2024-09-10&types=football,handball&source=live-arena';
      try {
        const response = await fetch(url);
        const data = await response.json();
        console.log('API Response:', data); 
        setMatches(data);
        setLoading(false); 
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false); 
      }
    };

