import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { addDays, subDays, format, parseISO, isValid } from 'date-fns';

const LiveArena = () => {
  const [fetchedData, setFetchedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedSport, setSelectedSport] = useState('all');
  const [availableDates, setAvailableDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date(2024, 8, 7)); // Set default to 7th September 2024

  // Fetch all available dates when the component mounts
  useEffect(() => {
    const fetchAvailableDates = async () => {
      try {
        const response = await fetch(
          'https://www.rtl.lu/api/sportstream/livestreams?start_date=2024-09-01&end_date=2024-10-01&source=live-arena'
        );
        const data = await response.json();

        // Parse and validate the dates returned by the API
        const dates = Object.keys(data).map((date) => {
          const parsedDate = parseISO(date); // Parse the date string
          return isValid(parsedDate) ? parsedDate : null; // Filter out invalid dates
        }).filter(Boolean); // Remove null values

        setAvailableDates(dates); // Set available dates for the calendar
        setLoading(false);
      } catch (error) {
        console.error('Error fetching available dates:', error);
        setLoading(false);
      }
    };
    fetchAvailableDates();
  }, []);

  // Fetch data based on selected date and sport
  useEffect(() => {
    if (!selectedDate) return;

    const fetchData = async () => {
      const formattedDate = format(selectedDate, 'yyyy-MM-dd');
      console.log(`Fetching data for: ${formattedDate}`); // Debugging

      let url = `https://www.rtl.lu/api/sportstream/livestreams?start_date=${formattedDate}&end_date=${formattedDate}&source=live-arena`;

      if (selectedSport !== 'all') {
        url += `&types=${selectedSport}`;
      }

      try {
        const response = await fetch(url);
        const data = await response.json();
        setFetchedData(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [selectedDate, selectedSport]);

  // Render fetched data
  const renderData = (entries) => {
    return Object.keys(entries).map((date) => (
      <div key={date}>
        <div className="match-date-header">
          {formatDate(date)}
        </div>
        {entries[date].map((match, index) => (
          <div key={index} className="match-row">
            <div>{match.video.match.home_team.name} vs {match.video.match.visitor_team.name}</div>
          </div>
        ))}
      </div>
    ));
  };

  const formatDate = (date) => {
    const parsedDate = new Date(date);
    if (!isValid(parsedDate)) {
      return 'Invalid date';
    }
    return format(parsedDate, 'dd MMMM yyyy');
  };

  // Check if the date is selectable based on available dates from the API
  const isDateSelectable = (date) => {
    return availableDates.some(
      (availableDate) => format(availableDate, 'yyyy-MM-dd') === format(date, 'yyyy-MM-dd')
    );
  };

  // Handle sport selection change
  const handleSportChange = (event) => {
    setSelectedSport(event.target.value);
  };

  return (
    <div>
      <div className="header-image">
        <img src="/images/header-image.png" alt="header-image-rtl" />
      </div>
      <div className="line-1"></div>
      <div className="content-header">
        <h3>Replay</h3>
        <div className="inputs-div">
          <label htmlFor="sport-select">Select Sport:</label>
          <select id="sport-select" value={selectedSport} onChange={handleSportChange}>
            <option value="all">All</option>
            <option value="football">Football</option>
            <option value="handball">Handball</option>
            <option value="basketball">Basketball</option>
            <option value="volleyball">Volleyball</option>
            <option value="table-tennis">Table Tennis</option>
            <option value="cycling">Cycling</option>
          </select>

          <label>Select Date:</label>
          <DatePicker
            selected={selectedDate}
            onChange={(date) => setSelectedDate(date)}
            filterDate={isDateSelectable} // Only enable dates that are available
            highlightDates={availableDates}
            dateFormat="dd/MM/yyyy"
            showMonthDropdown
            showYearDropdown
            dropdownMode="select"
            minDate={subDays(new Date(), 30)} // Limit selection to the past 30 days
            maxDate={addDays(new Date(), 30)} // Limit selection to the next 30 days
          />
        </div>
      </div>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        Object.keys(fetchedData).map((sportType) => (
          <div key={sportType} className="match-header">
            <h2>{sportType.charAt(0).toUpperCase() + sportType.slice(1)}</h2>
            {Object.keys(fetchedData[sportType]).map((competitionKey) => (
              <div key={competitionKey} className="competition-section">
                <h3>{fetchedData[sportType][competitionKey].competition.name}</h3>
                {renderData(fetchedData[sportType][competitionKey].entries)}
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default LiveArena;
