import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const LiveArena = () => {
  const [fetchedData, setFetchedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedSport, setSelectedSport] = useState('all');
  const [selectedCompetition, setSelectedCompetition] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date('2024-09-07'));
  const [datesWithMatches, setDatesWithMatches] = useState([]);
  const [filterOption, setFilterOption] = useState('sport'); // New filter state (sport or date)

  useEffect(() => {
    const fetchData = async () => {
      let url = 'https://www.rtl.lu/api/sportstream/livestreams?start_date=2024-09-03&end_date=2024-09-10&source=live-arena';
      if (selectedSport !== 'all') {
        url += `&types=${selectedSport}`;
      }
      try {
        const response = await fetch(url);
        const data = await response.json();
        console.log('API Response:', data);

        // Extract dates with matches
        const allDates = new Set();
        Object.keys(data).forEach(sportType => {
          Object.keys(data[sportType]).forEach(competitionKey => {
            Object.keys(data[sportType][competitionKey].entries).forEach(date => {
              allDates.add(date);
            });
          });
        });

        setDatesWithMatches(Array.from(allDates).map(date => new Date(date)));
        setFetchedData(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [selectedSport]);

  useEffect(() => {
    if (selectedDate) {
      const formattedDate = selectedDate.toISOString().split('T')[0];
      const fetchDateData = async () => {
        let url = `https://www.rtl.lu/api/sportstream/livestreams?start_date=${formattedDate}&end_date=${formattedDate}&source=live-arena`;
        if (selectedSport !== 'all') {
          url += `&types=${selectedSport}`;
        }
        try {
          const response = await fetch(url);
          const data = await response.json();
          console.log('API Response for date:', data);
          setFetchedData(data);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching date data:', error);
          setLoading(false);
        }
      };

      fetchDateData();
    }
  }, [selectedDate, selectedSport]);

  const formatDate = (date) => {
    const options = { day: '2-digit', month: 'long', year: 'numeric' };
    const formattedDate = new Intl.DateTimeFormat('en-GB', options).format(new Date(date));
    return formattedDate.replace(/^(\d{2})/, '$1.');
  };

  const renderData = (entries) => {
    if (!entries) return null;

    const sortedEntries = Object.keys(entries).sort((a, b) => {
      if (filterOption === 'date') {
        return new Date(entries[a][0].video.match.hour) - new Date(entries[b][0].video.match.hour); // Sort by match time
      }
      return new Date(entries[b][0].video.match.date).getTime() - new Date(entries[a][0].video.match.date).getTime(); // Sort by date
    });

    return sortedEntries.map((date) => (
      <div key={date}>
        <div className="match-date-header">
          {formatDate(date)}
        </div>
        {entries[date].map((match, index) => (
          <div key={index} className="match-row">
            <div className="match-row-left">
              <div className="match-time">
                <div className="match-hour">{match.video.match.hour}</div>
                <div className="match-date">{new Date(match.video.match.date).toLocaleDateString('en-GB')}</div>
              </div>
              <div className='team-image-left'>
                <img src={match.video.match.home_team.logo_url} alt={match.video.match.home_team.name} width="60" />
              </div>
              <div>{match.video.match.home_team.name}</div>
            </div>
            <div className="match-score">
              <div>
                {match.video.match['home_score']} : {match.video.match['visitor_score']}
              </div>
              <div className='play-div'>
                {/* Play button SVG */}
              </div>
            </div>
            <div className="match-row-right">
              <div>{match.video.match.visitor_team.name}</div>
              <div className='team-image-right'>
                <img src={match.video.match.visitor_team.logo_url} alt={match.video.match.visitor_team.name} width="60" />
              </div>
            </div>
          </div>
        ))}
      </div>
    ));
  };

  const handleSportChange = (event) => {
    setSelectedSport(event.target.value);
    setSelectedCompetition(''); // Reset competition when sport changes
  };

  const handleCompetitionChange = (event) => {
    setSelectedCompetition(event.target.value);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleFilterChange = (e) => {
    setFilterOption(e.target.value);
  };

  const filteredByDate = (data) => {
    if (!selectedDate) return data;

    const formattedDate = selectedDate.toISOString().split('T')[0];
    const filtered = {};

    Object.keys(data).forEach(sportType => {
      filtered[sportType] = {};
      Object.keys(data[sportType]).forEach(competitionKey => {
        const entries = data[sportType][competitionKey].entries;
        const filteredEntries = {};

        Object.keys(entries).forEach(date => {
          if (date === formattedDate) {
            filteredEntries[date] = entries[date];
          }
        });

        if (Object.keys(filteredEntries).length) {
          filtered[sportType][competitionKey] = { entries: filteredEntries };
        }
      });

      if (Object.keys(filtered[sportType]).length === 0) {
        delete filtered[sportType];
      }
    });

    return filtered;
  };

  const sortedMatches = (data) => {
    if (!selectedDate) return data;

    const filtered = filteredByDate(data);

    return Object.keys(filtered).reduce((acc, sportType) => {
      acc[sportType] = {};
      Object.keys(filtered[sportType]).forEach(competitionKey => {
        acc[sportType][competitionKey] = { entries: filtered[sportType][competitionKey].entries };
      });
      return acc;
    }, {});
  };

  const matchesToDisplay = selectedDate ? sortedMatches(fetchedData) : fetchedData;

  return (
    <div>
      <div className='header-image'>
        <img src='/images/header-image.png' alt='header-image-rtl'/>
      </div>
      <div className='line-1'></div>
      <div className='content-header'>
        <h3>Replay</h3>
        <div className='inputs-div'>
          <label htmlFor='filter-select'>Filter By:</label>
          <select id='filter-select' value={filterOption} onChange={handleFilterChange}>
            <option value='sport'>Sport</option>
            <option value='date'>Date</option>
          </select>

          <label htmlFor='sport-select'>Select Sport:</label>
          <select id='sport-select' value={selectedSport} onChange={handleSportChange}>
            <option value='all'>All</option>
            <option value='football'>Football</option>
            <option value='handball'>Handball</option>
            <option value='basketball'>Basketball</option>
            <option value='volleyball'>Volleyball</option>
            <option value='table-tennis'>Table Tennis</option>
            <option value='cycling'>Cycling</option>
          </select>

          <label htmlFor='date-select'>Select Date:</label>
          <DatePicker
            id='date-select'
            selected={selectedDate}
            onChange={handleDateChange}
            minDate={new Date('2024-09-03')}
            maxDate={new Date('2024-09-10')}
            filterDate={date => datesWithMatches.some(d => d.toDateString() === date.toDateString())}
          />
        </div>
      </div>
      <div className='line-2'></div>

      <div className='match-list'>
        {loading ? (
          <p>Loading matches...</p>
        ) : (
          Object.keys(matchesToDisplay).map((sportType) => (
            <div key={sportType}>
              <h2 className="sport-title">{sportType.toUpperCase()}</h2>
              {Object.keys(matchesToDisplay[sportType]).map((competitionKey) => (
                <div key={competitionKey}>
                  <h3 className="competition-title">{competitionKey}</h3>
                  {renderData(matchesToDisplay[sportType][competitionKey].entries)}
                </div>
              ))}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LiveArena;
