import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const LiveArena = () => {
  const [fetchedData, setFetchedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedSport, setSelectedSport] = useState('all');
  const [selectedCompetition, setSelectedCompetition] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date('2024-09-07'));
  const [datesWithMatches, setDatesWithMatches] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      let url = 'https://www.rtl.lu/api/sportstream/livestreams?start_date=2024-09-03&end_date=2024-09-10&source=live-arena';
      if (selectedSport !== 'all') {
        url += `&types=${selectedSport}`;
      }
      try {
        const response = await fetch(url);
        const data = await response.json();
        console.log('API Response:', data);

        // Extract dates with matches
        const allDates = new Set();
        Object.keys(data).forEach(sportType => {
          Object.keys(data[sportType]).forEach(competitionKey => {
            Object.keys(data[sportType][competitionKey].entries).forEach(date => {
              allDates.add(date);
            });
          });
        });

        setDatesWithMatches(Array.from(allDates).map(date => new Date(date)));
        setFetchedData(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [selectedSport]);

  useEffect(() => {
    if (selectedDate) {
      const formattedDate = selectedDate.toISOString().split('T')[0];
      const fetchDateData = async () => {
        let url = `https://www.rtl.lu/api/sportstream/livestreams?start_date=${formattedDate}&end_date=${formattedDate}&source=live-arena`;
        if (selectedSport !== 'all') {
          url += `&types=${selectedSport}`;
        }
        try {
          const response = await fetch(url);
          const data = await response.json();
          console.log('API Response for date:', data);
          setFetchedData(data);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching date data:', error);
          setLoading(false);
        }
      };

      fetchDateData();
    }
  }, [selectedDate, selectedSport]);

  const formatDate = (date) => {
    const options = { day: '2-digit', month: 'long', year: 'numeric' };
    const formattedDate = new Intl.DateTimeFormat('en-GB', options).format(new Date(date));
    return formattedDate.replace(/^(\d{2})/, '$1.');
  };

  const renderData = (entries) => {
    if (!entries) return null;

    // Sort entries by time
    const sortedEntries = Object.keys(entries).sort((a, b) => new Date(entries[b][0].video.match.date).getTime() - new Date(entries[a][0].video.match.date).getTime());

    return sortedEntries.map((date) => (
      <div key={date}>
        <div className="match-date-header">
          {formatDate(date)}
        </div>
        {entries[date].map((match, index) => (
          <div key={index} className="match-row">
            <div className="match-row-left">
              <div className="match-time">
                <div className="match-hour">{match.video.match.hour}</div>
                <div className="match-date">{new Date(match.video.match.date).toLocaleDateString('en-GB')}</div>
              </div>
              <div className='team-image-left'>
                <img src={match.video.match.home_team.logo_url} alt={match.video.match.home_team.name} width="60" />
              </div>
              <div>{match.video.match.home_team.name}</div>
            </div>
            <div className="match-score">
              <div>
                {match.video.match['home_score']} : {match.video.match['visitor_score']}
              </div>
              <div className='play-div'>
